<?php  class Object {   protected $_data;   protected $_dummy;   public function __construct($a = NULL)   {    $this->NewRow();    if(!is_null($a))     $this->_data = &$a;    $this->_dummy = false;   }   public function NewRow()   {    $this->_data = array();   }   public function __set($aName, $aValue)   {    $this->_data[$aName] = $aValue;   }   public function __get($aName)   {    if($this->_dummy)     return 0;    return $this->_data[$aName];   }   public function __isset($aName)   {    return isset($this->_data[$aName]);   }   public function __unset($aName)   {    unset ($this->_data[$aName]);   }   public function IsLoad()   {    if(!is_array($this->_data))     $this->_data = array();    return !empty($this->_data);   }   public function &GetArray()   {    return $this->_data;   }  }  ?>