<?php  require_once('.//lib/form.php');  require_once('.//lib/dbo.php');  require_once('.//eng/main/troop.php');  require_once('.//lib/defines.php');  if($town->$u == 0)  {   echo $lang['BuildBuilding'].'</div>';   return;  }  $research = array();  $sql = $dbo->ExectueQuery(sprintf('SELECT * FROM `%sresearch` WHERE `kind` = \'%s\' AND `tid` = \'%s\' AND `d` = \'0\'',   DB_PERFIX, RES_TROOP, $town->id));  while($row = $dbo->Read($sql))   $research[$row['subkind']] = $row;  $dbo->Cancel($sql);  gc_collect_cycles();  $one = 1;  if(isset($_POST['com']) and $_POST['com'] == RESEARCH_TROOP)  {   $_POST['kind'] = isset($_POST['kind']) ? ValidNumber($_POST['kind'],true): 0;   if($_POST['kind'] and !($town->res_u & ($one << $_POST['kind'] - 1)) and ($_POST['kind'] != 13 or $_POST['kind'] !=12))   {    if(!isset($research[$_POST['kind']]))    {     $count = $dbo->ExectueScaler(sprintf('SELECT COUNT(*) AS `c` FROM `%sresearch` WHERE `tid` = \'%s\' AND `kind` = \'%s\' AND `d` = \'0\'',       DB_PERFIX, $town->id, RES_TROOP), 'c');     if(!($count > 1 or ($count and !$plus->HavePlus('pb'))))     {      if($account->Lock())      {       $town = $account->GetTown($town->id);       $row = $dbo->ExectueRow(sprintf('SELECT * FROM `troop_info` WHERE `id` = \'%s\'',($account->kind -1)*14 + $_POST['kind']));       if($town->HaveEnough($row['r1'] * 10, $row['r2'] * 10,$row['r3'] * 10,$row['r4'] * 10, $row['r5']*10))       {        $bt = ceil($_SERVER['REQUEST_TIME'] + ($row['times'] - ($row['times'] * $town->$u * 0.03))* 50);        $town->Research($_POST['kind'], $row['r1'] * 10, $row['r2'] * 10, $row['r3'] * 10, $row['r4'] * 10, $row['r5']*10, $bt, RES_TROOP);       }      }      $account->UnLock();     }    }   }  }  $research = array();  $sql = $dbo->ExectueQuery(sprintf('SELECT * FROM `%sresearch` WHERE `kind` = \'%s\' AND `tid` = \'%s\' AND `d` = \'0\'',   DB_PERFIX, RES_TROOP, $town->id));  while($row = $dbo->Read($sql))   $research[$row['subkind']] = $row;  $dbo->Cancel($sql);  gc_collect_cycles();  $sql = $dbo->ExectueQuery(sprintf('SELECT * FROM `%stroop_info` WHERE `kind` = \'%s\' ORDER BY `id`',DB_PERFIX,$account->kind));  $i = 0;  $j = 0;  $list = array();  while($row = $dbo->Read($sql))  {   $i++;   $list[$i] = new Object($row);   $form->AddModal('mo'.$i,$row['name'],sprintf('<img src="img/troop/b/%d_%d.gif" class="bigimg" alt="%s"/>%s',    $account->kind,$i,$row['name'],$row['desc']),$form->TroopInfo(    $row['r1'],$row['r2'],$row['r3'],$row['r4'],$row['r5'],    SecToString($row['times']),$row['used'],    $troops[$account->kind][$i][0],$troops[$account->kind][$i][1],$troops[$account->kind][$i][2],    $troops[$account->kind][$i][3],$troops[$account->kind][$i][4],$troops[$account->kind][$i][5]));  }  $dbo->Cancel($sql);  gc_collect_cycles();  ?>
