<?php  $i = 0;  $count = count($research);  $j =0;  foreach($list as &$l)  {   $i++;      if($i > $town->$u + 4)          continue;   if(($town->res_u &($one <<($i - 1))) or ( $i > 12))    continue;   $j++;   printf('<tr><td><img src="img/troop/s/%d_%d.gif" class = "i30s" onclick="modal.Show(\'mo%d\');" alt = "%s" /></td><td>',    $account->kind,$i,$i,$l->name);   $acthion = $town->HaveEnough($l->r1 * 10, $l->r2 * 10, $l->r3 * 10, $l->r4 * 10, $l->r5 * 10)?    sprintf('<input type="submit" value="%s" onclick="SetValues(\'kind\',\'%d\');"  />',$lang['Research'], $i):    sprintf($lang['NotEnough'],$lang['Resource']);   if($count > 1 or ($count and !$plus->HavePlus('pb')))    $acthion = sprintf($lang['MaxPermission'],$lang['Research']);   if(isset($research[$i]))    $acthion = $form->Addtimer($research[$i]['modify'],sprintf('building.php?bid=%s&tid=%s',$bid,$town->id));   $bt = ceil(($l->times - ($l->times * $town->$u * 0.03))* 50);   $form->EchoCost($l->r1 * 10,$l->r2 * 10,$l->r3 * 10,$l->r4 * 10,$l->r5 * 10, SecToString($bt),0);      printf('</td><td>%s</td></tr>', $acthion);  }  if(!$j)   printf('<tr><td colspan="3">%s</td></tr>',$lang['NoRecord']);  ?>
